import React, { Component } from 'react';
import './App.css';
import Bank from './Bank/Bank'
import { connect } from 'react-redux';

class App extends Component {

render() { 

  let transactionHistory = (
   <div>
    {this.props.transactionHistory.map((log) => { return <li>{log.transactionType} ${log.amount} | closing balance: ${log.newBalance} | {log.date} </li> })} 
   </div>
  )

    return (
      <div className="container">

<div className="card mt-5"><div className="card-body"> <div className="App">
      <header className="App-header">
          <h1>🏧</h1>

      </header>

      <h1>Account Balance: {this.props.balance}</h1>
<div className="row">
<div className="col-sm-6">
      <div className="atm">    
        <h2>Withdraw Amount</h2>
        <button className="mainbtn btn btn-md btn-primary" onClick={() => this.props.withdraw(500)}>Withdraw 500</button>
        <button className="mainbtn btn btn-md btn-primary" onClick={() => this.props.withdraw(100)}>Withdraw 100</button>
      </div>
</div>
<div className="col-sm-6">

      <Bank />
</div>
</div>
<hr/>
      <div>
        <h1>Transaction History</h1>
        {transactionHistory}
      </div>

    </div></div></div></div>
    );
  }
}

const mapStateToProps = state => {
  return { 
    balance: state.balance,
    transactionHistory: state.transactionHistory
  }
}

const mapDispatchToProps = dispatch => {
  return {
    //in last app values were hard coded, now we pass a payload depending on which button is clicked
    withdraw: (amount) => dispatch({type:'withdraw', value: amount}),
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
  )(App);
